import React from 'react';
//import { getApiURL } from '../Utils';
import emitter from '../emitter';

class Results extends React.PureComponent {

    constructor(props) {
        super(props)
        this.state = {data: {}};
    }

    async getSearchResult(searchValue) {
        console.log("result: " + searchValue);

        const limit = 25;
        const itunesURL = `https://itunes.apple.com/search?term=${searchValue.split(' ').join('+')}&limit=${limit}`
        const response = await fetch(itunesURL);
        const json = await response.json();


        console.log(json)
        this.setState({data: json});
    }

    componentDidMount() {
        emitter.on('search', this.getSearchResult.bind(this))
    }

    componentWillUnmount() {
        emitter.removeListener('search');
    }

    render() {
        return (
            <div>
                {this.state.data.length > 0 ? <List results={this.state.data}/> : "no results"}
            </div>
        )
    }
    
}

export default Results;
