import React from 'react';
import emitter from '../emitter';
import apis from '../api/index'

class SearchForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {value: ''};

    }

    handleChange = (event) => {
        this.setState({value: event.target.value});
    }

    handleSubmit = (event) => {
        console.log("search: " + this.state.value);
        emitter.emit('search', this.state.value);
        event.preventDefault();
    }

    showPopular = (event) => {
        let result = apis.getPopularSearchQuery();
        console.log(result)
    }

    render() {
        return (
            <nav className="navbar navbar-light bg-light d-flex justify-content-center">
            <form className="form-inline d-flex  ">
                <input type="text" value={this.state.value} placeholder="Search"
                           onChange={this.handleChange} className="form-control mr-sm-2"/>
                <input type="submit" value="Submit" className="btn btn-primary" onClick={this.handleSubmit}/>
                <button onClick={this.showPopular} className="btn btn-primary" style={{margin:"5px"}}>show 10</button>
            </form>
            </nav>
        )
    }
}

export default SearchForm;
