import React from 'react';
//import { getApiURL } from '../Utils';
import emitter from '../emitter';
import List from './List'

class Results extends React.PureComponent {

    constructor(props) {
        super(props)
        this.state = {data: {}};
    }

    async getSearchResult(searchValue) {
        console.log("result: " + searchValue);

        const limit = 25;
        const itunesURL = `https://itunes.apple.com/search?term=${searchValue.split(' ').join('+')}&limit=${limit}`
        const response = await fetch(itunesURL);
        const json = await response.json();


        console.log(json.results)
        this.setState({data: json.results});
    }

   

    render() {
        return (
            <div>
                {this.props.data.length > 0 ? <List results={this.props.data}/> : "no results"}
            </div>
        )
    }
    
}

export default Results;
